import {
  SIGN_IN,
  SIGN_OUT,
  CREATE_STREAM,
  DELETE_STREAM,
  EDIT_STREAM,
  FETCH_STREAM,
  FETCH_STREAMS,
} from "./types";
import stream from "../apis/streams";

export const createStream = (formValues) => async (dispatch, getState) => {
  const { userId } = getState().auth;
  const response = await stream.post("/streams", {
    ...formValues,
    userId,
  });
  dispatch({ type: CREATE_STREAM, payload: response.data });
  history.push("/");
};

export const editStream = async (id, formValues) => async (
  dispatch,
  getState
) => {
  const response = await stream.put(`/streams/${id}`, formValues);
  dispatch({ type: EDIT_STREAM, payload: response.data });
};

export const deleteStream = async (id) => async (dispatch, getState) => {
  const response = await stream.delete(`/streams/${id}`);
  dispatch({ type: DELETE_STREAM, payload: id });
};

export const getStream = async (id) => async (dispatch, getState) => {
  const response = await stream.get(`/streams/${id}`);
  dispatch({ type: FETCH_STREAM, payload: response.data });
};

export const getStreams = () => async (dispatch, getState) => {
  const response = await stream.get("/streams");

  dispatch({ type: FETCH_STREAMS, payload: response.data });
};

export const signIn = (userId) => {
  return {
    type: SIGN_IN,
    payload: userId,
  };
};

export const signOut = (id) => {
  return {
    type: SIGN_OUT,
  };
};
