import React from "react";
import { Field, reduxForm } from "redux-form";
import { form } from "redux-forms/lib/containers";

const CreateStream = ({ handleSubmit }) => {
  const renderInput = ({ input, label }) => {
    return (
      <div className="field">
        <div>{label}</div>
        <input {...input} autoComplete="off" />
      </div>
    );
  };
  const renderError = () => {
    return <div>errors</div>;
  };
  const formSubmit = (formValues) => {
    console.log(formValues);
  };

  return (
    <form onSubmit={handleSubmit(formSubmit)}>
      <Field name="title" component={renderInput} label="Title" />
      <Field name="description" component={renderInput} label="Description" />
      <br />
      <button type="submit" className="ui primary button">
        Create
      </button>
    </form>
  );
};

const validate = (values) => {
  const errors = {};
  if (!values.title) {
    errors.title = "You must enter a title";
  }
  if (!values.description) {
    errors.description = "You must enter a description";
  }
  return errors;
};

const formWrapped = reduxForm({ form: "streamCreate", validate })(CreateStream);

