import { combineReducers } from "redux";

const onSignIn = (state = null, action) => {
  if (action.type === "SIGN_IN") {
    return { isSignedIn: true };
  }
  return state;
};

const onSignOut = (state = null, action) => {
  if (action.type === "SIGN_OUT") {
    return state;
  }
  return state;
};

export default combineReducers({
  signin: onSignIn,
  signOut: onSignOut,
});
