import React, { useEffect, useState, useRef } from "react";
import { connect } from "react-redux";
import { signIn, signOut } from "../actions";

const GoogleAuth = (props) => {
  const [signedIn, setsignedIn] = useState({ isSignedIn: null });
  const auth = useRef();
  useEffect(() => {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "398671583439-chdv5bu5cish5kdrbfjfdmil9vfsgsnb.apps.googleusercontent.com",
          scope: "email",
        })
        .then((value) => {
          auth.current = window.gapi.auth2.getAuthInstance();
          setsignedIn({ isSignedIn: auth.current.isSignedIn.get() });
          auth.current.isSignedIn.listen(onAuthChange);
        });
    });
  }, []);

  const onAuthChange = (isSignedIn) => {
    if (isSignedIn) {
      props.signIn();
    } else {
      props.signOut(0);
    }
  };

  const onSignInClick = () => {
    auth.current.signIn();
  };

  const onSignOutClick = () => {
    auth.current.signOut();
  };

  const renderAuthButton = () => {
    if (signedIn.isSignedIn === null) {
      return (
        <div>
          <button onClick={onSignInClick} className="ui red google button">
            <i className="google icon" />
            Sign in with google
          </button>
        </div>
      );
    } else if (signedIn.isSignedIn) {
      return (
        <div>
          <button onClick={onSignOutClick} className="ui red google button">
            <i className="google icon" />
            Sign Out
          </button>
        </div>
      );
    } else {
      return (
        <div>
          <button onClick={onSignInClick} className="ui red google button">
            <i className="google icon" />
            Sign in with google
          </button>
        </div>
      );
    }
  };

  return <div>{renderAuthButton()}</div>;
};

const mapStateToProps = (state) => {
  return { isSignedIn: state.isSignedIn };
};

export default connect(mapStateToProps, { signIn, signOut })(GoogleAuth);
