import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getStream as fetchStream, editStream } from "../../actions/index";
import StreamForm from "./StreamForm";

const EditStream = ({ stream, fetchStream, id }) => {
  useEffect(() => {
    fetchStream(id);
  }, []);

  const onSubmit = (formValues) => {
    console.log(formValues);
    const response = editStream(formValues);
  };

  if (!stream) {
    return <div>Loading...</div>;
  }
  return (
    <div>
      <StreamForm
        onSubmit={onSubmit}
        initialValues={{ title: stream.title, description: stream.description }}
      />
    </div>
  );
};

const mapStateToProps = (state, ownProps) => {
  const id = ownProps.match.params.id;
  return { id: id, stream: state.streams[id] };
};

export default connect(mapStateToProps, { fetchStream, editStream })(
  EditStream
);
