import React, { useEffect, useState, useRef } from "react";

const GoogleAuth = () => {
  const [signedIn, setsignedIn] = useState({ isSignedIn: null });
  const auth = useRef();
  useEffect(() => {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "398671583439-chdv5bu5cish5kdrbfjfdmil9vfsgsnb.apps.googleusercontent.com",
          scope: "email",
        })
        .then((value) => {
          auth.current = window.gapi.auth2.getAuthInstance();
          setsignedIn({ isSignedIn: auth.current.isSignedIn.get() });
          auth.current.isSignedIn.listen(onAuthChange(auth.current));
        });
    });
  }, []);

  const onAuthChange = () => {
    console.log(auth.current);
    setsignedIn({ isSignedIn: auth.current.isSignedIn.get() });
  };

  const onSignIn = () => {
    auth.current.signIn();
  };

  const onSignOut = () => {
    auth.current.signOut();
  };

  const renderAuthButton = () => {
    if (signedIn.isSignedIn === null) {
      return (
        <div>
          <button onClick={onSignIn} className="ui red google button">
            <i className="google icon" />
            Sign in with google
          </button>
        </div>
      );
    } else if (signedIn.isSignedIn) {
      return (
        <div>
          <button className="ui red google button">
            <i className="google icon" />
            Sign Out
          </button>
        </div>
      );
    } else {
      return (
        <div>
          <button onClick={onSignIn} className="ui red google button">
            <i className="google icon" />
            Sign in with google
          </button>
        </div>
      );
    }
  };

  return <div>{renderAuthButton()}</div>;
};

export default GoogleAuth;
