import {
  SIGN_IN,
  SIGN_OUT,
  DELETE_STREAM,
  UPDATE_STREAM,
  FETCH_STREAM,
  FETCH_STREAMS,
} from "./types";
import stream from "../apis/streams";

export const createStream = async () => async (dispatch, getState) => {
  const response = await stream.post("/");
  dispatch({ type: CREATE_STREAM, payload: response });
};

export const updateStream = async () => async (dispatch, getState) => {
  const response = await stream.put("/");
  dispatch({ type: CREATE_STREAM, payload: response });
};

export const deleteStream = async () => async (dispatch, getState) => {
  const response = await stream.patch("/");
  dispatch({ type: CREATE_STREAM, payload: response });
};

export const getStream = async () => async (dispatch, getState) => {
  const response = await stream.get("/");
  dispatch({ type: CREATE_STREAM, payload: response });
};

export const getStreams = async () => async (dispatch, getState) => {
  const response = await stream.get("/");
  dispatch({ type: CREATE_STREAM, payload: response });
};

export const signIn = (userId) => {
  return {
    type: SIGN_IN,
    payload: userId,
  };
};

export const signOut = (id) => {
  return {
    type: SIGN_OUT,
  };
};
