import { combineReducers } from "redux";

const INITIAL_STATE = {
  isSignedIn: null,
};
export default (state = INITIAL_STATE, action) => {
  if (action.type === "SIGN_IN") {
    return { isSignedIn: true };
  }
  return state;
};
