import { render } from "@testing-library/react";
import React, { useEffect, useState } from "react";

const GoogleAuth = () => {
  const [signedIn, setsignedIn] = useState({ isSignedIn: null });
  useEffect(() => {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "398671583439-chdv5bu5cish5kdrbfjfdmil9vfsgsnb.apps.googleusercontent.com",
          scope: "email",
        })
        .then((value) => {
          const auth = window.gapi.auth2.getAuthInstance();
          setsignedIn({ isSignedIn: auth.isSignedIn.get() });
          auth.isSignedIn.listen(onAuthChange(auth));
        });
    });
  }, []);

  const onAuthChange = (auth) => {
    setsignedIn({ isSignedIn: auth.isSignedIn.get() });
  };

  const renderAuthButton = () => {
    if (signedIn.isSignedIn === null) {
      return (
        <div>
          <button className="ui green google button">
            <i className="google icon" />
            Sign in
          </button>
        </div>
      );
    } else if (signedIn.isSignedIn) {
      return (
        <div>
          <button className="ui red google button">
            <i className="google icon" />
            Sign Out
          </button>
        </div>
      );
    } else {
      return <div>Not Signed in </div>;
    }
  };

  return <div>{renderAuthButton()}</div>;
};

export default GoogleAuth;
