import {
  SIGN_IN,
  SIGN_OUT,
  CREATE_STREAM,
  DELETE_STREAM,
  UPDATE_STREAM,
  FETCH_STREAM,
  FETCH_STREAMS,
} from "./types";
import stream from "../apis/streams";

export const createStream = (formValues) => async (dispatch, getState) => {
  const response = await stream.post("/streams", formValues);
  dispatch({ type: CREATE_STREAM, payload: response.data });
};

export const editStream = async () => async (dispatch, getState) => {
  const response = await stream.put("/");
  dispatch({ type: EDIT_STREAM, payload: response });
};

export const deleteStream = async () => async (dispatch, getState) => {
  const response = await stream.patch("/");
  dispatch({ type: DELETE_STREAM, payload: response });
};

export const getStream = async () => async (dispatch, getState) => {
  const response = await stream.get("/");
  dispatch({ type: FETCH_STREAM, payload: response });
};

export const getStreams = async () => async (dispatch, getState) => {
  const response = await stream.get("/");
  dispatch({ type: FETCH_STREAMS, payload: response });
};

export const signIn = (userId) => {
  return {
    type: SIGN_IN,
    payload: userId,
  };
};

export const signOut = (id) => {
  return {
    type: SIGN_OUT,
  };
};
