import React from "react";
import { Field, reduxForm } from "redux-form";
import { form } from "redux-forms/lib/containers";

const CreateStream = () => {
  const renderInput = ({ formProps, label }) => {
    return (
      <div className="field">
        <div>{label}</div>
        <input {...formProps} autoComplete="off" />
      </div>
    );
  };
  const renderError = () => {
    return <div>errors</div>;
  };
  const formSubmit = () => {};

  return (
    <form onSubmit={formSubmit}>
      <Field name="title" component={renderInput} label="Title" />
      <Field name="description" component={renderInput} label="Description" />
      <br />
      <button type="submit" className="ui primary button">
        Create
      </button>
    </form>
  );
};

const validate = () => {
  if (!form.title) {
    return <div>sjkdf</div>;
  }
};

export default reduxForm({ form: "streamCreate", validate })(CreateStream);
